                CantidadAnt: { type: Number },
                CantidadAct: { type: Number },
                IdTipoMovtoOK: { type: String },
                IdClaseMovtoOK: { type: String },
                Referencia: { type: String },
                detail_row: {
                    Activo: { type: String },
                    Borrado: { type: String },
                    detail_row_reg: {
                        FechaReg: { type: Date, default: Date.now },
                        UsuarioReg: { type: String }
                    },
                },
            },
            ],
            series: [{
                Serie: { type: String },
                Placa: { type: String },
                Observacion: { type: String },
                estatus_fisico: [{
                    IdTipoEstatusOK: { type: String },
                    Actual: { type: String },
                    Observacion: { type: String },
                    detail_row: {
                        Activo: { type: String },
                        Borrado: { type: String },
                        detail_row_reg: {
                            FechaReg: { type: Date, default: Date.now },
                            UsuarioReg: { type: String }
                        },
                    },
                },
                ],
                estatus_venta: [{
                    IdTipoEstatusOK: { type: String },
                    Actual: { type: String },
                    Observacion: { type: String },
                    detail_row: {
                        Activo: { type: String },
                        Borrado: { type: String },
                        detail_row_reg: {
                            FechaReg: { type: Date, default: Date.now },
                            UsuarioReg: { type: String }
                        },
                    },
                },
                ],
                ubicaciones: [{
                    IdAlmacenOK: { type: String },
                    Ubicacion: { type: String },
                    Actual: { type: String },
                    detail_row: {
                        Activo: { type: String },
                        Borrado: { type: String },
                        detail_row_reg: {
                            FechaReg: { type: Date, default: Date.now },
                            UsuarioReg: { type: String }
                        },
                    },
                },
                ],
                detail_row: {
                    Activo: { type: String },
                    Borrado: { type: String },
                    detail_row_reg: {
                        FechaReg: { type: Date, default: Date.now },
                        UsuarioReg: { type: String }
                    },
                },
            },
            ],
            },
        ],
        detail_row: {
            Activo: { type: String },
            Borrado: { type: String },
            detail_row_reg: {
                FechaReg: { type: Date, default: Date.now },
                UsuarioReg: { type: String }
            },
        },
    },
    ],
    detail_row: {
        Activo: { type: String },
        Borrado: { type: String },
        detail_row_reg: {
            FechaReg: { type: Date, default: Date.now },
            UsuarioReg: { type: String }
        },
    },
});

//Exportar modelo
export default mongoose.model(
    'cat_prod_serv',
    prodservSchema,
    'cat_prod_serv'
  );

