import * as mongoose from 'mongoose';

//Commerce
const prodservSchema = new mongoose.Schema({
    Series: [
        {
    Serie: { type : String, required : true },
    Placa: { type : String },
    Observacion: { type : String },
    estatus_fisico: [{  IdTipoEstatusOK:{type : String},
        Actual: {type: String},
        Observacion: {type: String},
        detail_row: [{
            Activo: {type: String},
            Borrado: {type: String},
        }]
     }],
    ubicaciones: [
      {
        IdAlmacenOK: { type : String },
        Ubicacion: { type : String },
        Actual: { type : String },
        detail_row: [{
            Activo: {type: String},
            Borrado: {type: String},
        }]
        },
    ],}
]});

//Exportar modelo
export default mongoose.model(
    'cat_series',
    prodservSchema,
    'cat_series'
  );

