//Commerce
import { Router } from 'express';
import config from '../../../config/config.js';
// Import Routes
import prodServRoutes from './prodServ.routes.js';

//import ordersRoutes from './orders.routes';
const routerAPI = (app) => {
  const router = Router();
  const api = config.API_URL;
  app.use(api, router);
